import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/todos';

function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editText, setEditText] = useState('');
  const [editDate, setEditDate] = useState('');

  // Fetch all todos on component mount
  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      setLoading(true);
      const response = await axios.get(API_URL);
      setTodos(response.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch todos. Make sure the server is running!');
      console.error('Error fetching todos:', err);
    } finally {
      setLoading(false);
    }
  };

  // Add new todo
  const addTodo = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const response = await axios.post(API_URL, {
        title: input,
        completed: false,
        dueDate: dueDate || null
      });
      setTodos([response.data, ...todos]);
      setInput('');
      setDueDate('');
      setError('');
    } catch (err) {
      setError('Failed to add todo');
      console.error('Error adding todo:', err);
    }
  };

  // Toggle todo completion
  const toggleTodo = async (id, completed) => {
    try {
      const response = await axios.put(`${API_URL}/${id}`, {
        completed: !completed
      });
      setTodos(todos.map(todo =>
        todo._id === id ? response.data : todo
      ));
      setError('');
    } catch (err) {
      setError('Failed to update todo');
      console.error('Error updating todo:', err);
    }
  };

  // Delete todo
  const deleteTodo = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      setTodos(todos.filter(todo => todo._id !== id));
      setError('');
    } catch (err) {
      setError('Failed to delete todo');
      console.error('Error deleting todo:', err);
    }
  };

  // Start editing
  const startEdit = (id, currentText, currentDate) => {
    setEditingId(id);
    setEditText(currentText);
    setEditDate(currentDate ? currentDate.substring(0, 16) : '');
  };

  // Save edit
  const saveEdit = async (id) => {
    if (!editText.trim()) return;

    try {
      const response = await axios.put(`${API_URL}/${id}`, {
        title: editText,
        dueDate: editDate || null
      });
      setTodos(todos.map(todo =>
        todo._id === id ? response.data : todo
      ));
      setEditingId(null);
      setEditText('');
      setEditDate('');
      setError('');
    } catch (err) {
      setError('Failed to update todo');
      console.error('Error updating todo:', err);
    }
  };

  // Cancel edit
  const cancelEdit = () => {
    setEditingId(null);
    setEditText('');
    setEditDate('');
  };

  if (loading) {
    return (
      <div className="app">
        <div className="loading">Loading todos...</div>
      </div>
    );
  }

  return (
    <div className="app">
      <h1>üìù My Todos</h1>

      {error && <div className="error">{error}</div>}

      <form onSubmit={addTodo} className="todo-form">
        <input
          type="text"
          className="todo-input"
          placeholder="What needs to be done?"
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <input
          type="datetime-local"
          className="date-input"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
        <button type="submit" className="add-btn">
          Add
        </button>
      </form>

      {todos.length === 0 ? (
        <div className="empty-state">
          No todos yet. Add one above! üöÄ
        </div>
      ) : (
        <ul className="todo-list">
          {todos.map((todo) => (
            <li key={todo._id} className="todo-item">
              <input
                type="checkbox"
                className="todo-checkbox"
                checked={todo.completed}
                onChange={() => toggleTodo(todo._id, todo.completed)}
              />

              {editingId === todo._id ? (
                <>
                  <input
                    type="text"
                    className="edit-input"
                    value={editText}
                    onChange={(e) => setEditText(e.target.value)}
                    autoFocus
                  />
                  <input
                    type="datetime-local"
                    className="edit-date-input"
                    value={editDate}
                    onChange={(e) => setEditDate(e.target.value)}
                  />
                  <div className="todo-actions">
                    <button
                      className="save-btn"
                      onClick={() => saveEdit(todo._id)}
                    >
                      Save
                    </button>
                    <button
                      className="cancel-btn"
                      onClick={cancelEdit}
                    >
                      Cancel
                    </button>
                  </div>
                </>
              ) : (
                <>
                  <div className="todo-content">
                    <span
                      className={`todo-text ${todo.completed ? 'completed' : ''}`}
                    >
                      {todo.title}
                    </span>
                    {todo.dueDate && (
                      <span className="todo-date">
                        üìÖ {new Date(todo.dueDate).toLocaleString([], {
                          year: 'numeric',
                          month: 'short',
                          day: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </span>
                    )}
                  </div>
                  <div className="todo-actions">
                    <button
                      className="edit-btn"
                      onClick={() => startEdit(todo._id, todo.title, todo.dueDate)}
                    >
                      Edit
                    </button>
                    <button
                      className="delete-btn"
                      onClick={() => deleteTodo(todo._id)}
                    >
                      Delete
                    </button>
                  </div>
                </>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
